import Axios from "axios";
import React, { useEffect, useState } from "react";
import { Image } from "cloudinary-react";
import { TransverseLoading } from 'react-loadingg';
import { CSSTransition } from 'react-transition-group';
import "./Profile.css";


function Profile() {

    const [yourUploads, setYourUploads] = useState([]);
    const [isLoading, setIsLoading] = useState(false)
    useEffect(() => {
        setIsLoading(true)
        Axios.get(
            `http://localhost:3001/upload/byUser/${localStorage.getItem("username")}`
        ).then((response) => {
            setYourUploads(response.data);
        })
            .catch(err => {
                console.error(err);
            }).then(() => {
                setIsLoading(false)
            });
    }, [])

    return (

        <div className="Profile">
            {!isLoading && Object.entries(yourUploads).length ? (
                <>

                    <h1>{localStorage.getItem("username")}</h1>
                    {yourUploads.map((val, key) => {
                        return (
                            <CSSTransition
                                in={yourUploads}
                                timeout={400}
                                classNames="alert"
                                unmountOnExit
                                appear
                                onEntered={setYourUploads}
                                onExit={setYourUploads}
                            >
                                <div className="Post">
                                    <div className="Image">

                                        <Image cloudName="dlhqyhr1r" publicId={val.image} />
                                    </div>
                                    <div className="Content">
                                        <div className="title">
                                            {" "}
                                            {val.title} / by @{val.author}
                                        </div>
                                        <div className="description">{val.description}</div>
                                    </div>
                                    <div className="Engagement">{val.likes}</div>
                                </div>
                            </CSSTransition>
                        );
                    })}

                </>
            ) : (
                <TransverseLoading
                    className="spinner"
                    type="spin"
                    color="blue"
                    height="5%"
                    width="5%"
                />
            )}
        </div>


    );
}


export default Profile;
