import Axios from "axios";
import React, { useEffect, useState } from "react";
import { Image } from "cloudinary-react";
import "./Profile.css";
const urls = [
    "https://placeimg.com/100/100/any&rnd=" + Math.random(),
    "https://placeimg.com/100/100/any&rnd=" + Math.random(),
    "https://placeimg.com/100/100/any&rnd=" + Math.random()
];

function Profile() {
    const [yourUploads, setYourUploads] = useState([]);

    useEffect(() => {
        Axios.get(
            `http://localhost:3001/upload/byUser/${localStorage.getItem("username")}`
        ).then((response) => {
            setYourUploads(response.data);
        });
    });
    const [loading, setLoading] = useState(true);
    const counter = useRef(0);
    const imageLoaded = () => {
        counter.current += 1;
        if (counter.current >= urls.length) {
            setLoading(false);
        }
    }
    return (
        <div className="Profile">
            <h1>{localStorage.getItem("username")}</h1>
            {yourUploads.map((val, key) => {
                return (
                    <div className="Post">
                        <div className="Image">
                            <Image cloudName="dlhqyhr1r" publicId={val.image} />
                        </div>
                        <div className="Content">
                            <div className="title">
                                {" "}
                                {val.title} / by @{val.author}
                            </div>
                            <div className="description">{val.description}</div>
                        </div>
                        <div className="Engagement">{val.likes}</div>
                        <React.Fragment>
                            <div style={{ display: loading ? "block" : "none" }}>
                                Loading images,
    </div>
                            <div style={{ display: loading ? "none" : "block" }}>
                                {urls.map(url =>
                                    <img
                                        key={url}
                                        src={url}
                                        onLoad={imageLoaded} />)}
                            </div>
                        </React.Fragment>;
}
                    </div>
                );
            })}
        </div>
    );
}

export default Profile;
