import React, { useEffect, useState } from "react";
import "./Home.css";
import { Image } from "cloudinary-react";
import Axios from "axios";
import ThumbUpAltIcon from "@material-ui/icons/ThumbUpAlt";

function Home() {
    const [uploads, setUploads] = useState([]);
    const [likes, setLikes] = useState([]);
    useEffect(() => {
        if (!localStorage.getItem("loggedIn")) {
            localStorage.setItem("loggedIn", false);
        }
    }, []);

    useEffect(() => {
        Axios.get("http://localhost:3001/upload").then((response) => {
            setUploads(response.data);
        });
    }, []);
    useEffect(() => {
        Axios.get("http://localhost:3001/upload/like").then((response) => {
            setLikes(response.data);
        });
    }, []);

    const likePost = (id, key,) => {
        var tempLikes = uploads;
        tempLikes[key].likes = tempLikes[key].likes + 1;

        Axios.post("http://localhost:3001/upload/like", {
            userlike: localStorage.getItem("username"),
            postid: id,
        }).then((response) => {
            setUploads(tempLikes);
        });
    };


    return (
        <div className="Home">
            {uploads.map((val, key,) => {
                return (
                    <div className="Post">
                        <div className="Image">
                            <Image cloudName="dlhqyhr1r" publicId={val.image} />
                        </div>
                        <div className="Content">
                            <div className="title">
                                {" "}
                                {val.title} / by @{val.author}
                            </div>
                            <div className="description">{val.description}</div>
                        </div>
                        <div className="Like">
                            <ThumbUpAltIcon
                                id="likeButton"
                                onClick={() => {
                                    likePost(val.id, key);
                                }}
                            />
                            <div>[uploads] </div>
                            {likes.slice(1, likes.length).map((val, key,) => {
                                return (

                                    <div> {val.amount} </div>

                                );
                            })}

                        </div>
                    </div>
                );
            })}

        </div>
    );
}

export default Home;
